import React,{createContext,useState} from 'react'




const global =createContext();
const Context = ({children}) => {
    const [login, setLogin] = useState(false)

    const LoggedIn=(user)=>{
      setLogin(true)
    }
    const LogOut=()=>{
      setLogin(false)
    }
  return (
   <global.Provider value={{login,LoggedIn,LogOut}}>
    {children}
   </global.Provider>
  )
}

export default Context
export {global}
